// 颜色反馈生成函数（"01230"）
string compute_feedback(const string& guess, const string& correctword) {
    string result = "00000";
    string temp = correctword;
    for (int i = 0; i < 5; ++i) {
        if (guess[i] == temp[i]) {
            result[i] = '1';
            temp[i] = '0';
        }
    }
    for (int i = 0; i < 5; ++i) {
        if (result[i] == '1') continue;
        for (int j = 0; j < 5; ++j) {
            if (temp[j] == guess[i]) {
                result[i] = '2';
                temp[j] = '0';
                break;
            }
        }
        if (result[i] == '0') result[i] = '3';
    }
    return result;
}

// elimination 函数（线程安全，局部变量替代）
int elimination(const string &guess, const string &correctword, const int b_init[10][26], const int p_init[26]) {
    int bb[10][26], pp[26];
    memcpy(bb, b_init, sizeof(bb));
    memcpy(pp, p_init, sizeof(pp));

    string s2 = compute_feedback(guess, correctword);

    for (char ch = 'a'; ch <= 'z'; ++ch) {
        vector<int> v1, v2, v3;
        for (int j = 0; j < 5; ++j) {
            if (guess[j] == ch) {
                switch(s2[j]) {
                    case '1': v1.push_back(j); break;
                    case '2': v2.push_back(j); break;
                    case '3': v3.push_back(j); break;
                }
            }
        }
        if (!v2.size() && v3.size()) for (int k = 0; k < 5; ++k) bb[k][ch - 'a'] = 1;
        if (!v1.empty()) for (int pos : v1) { for (int j = 0; j < 26; ++j) bb[pos][j] = 1; bb[pos][ch - 'a'] = 0; }
        if (!v2.empty()) { pp[ch - 'a'] = 1; for (int pos : v2) bb[pos][ch - 'a'] = 1; }
        if (!v2.empty() && v3.size()) for (int pos : v3) bb[pos][ch - 'a'] = 1;
    }

    int eliminated = 0;
    for (const string& word : wo) {
        bool valid = true;
        bitset<26> q;
        for (int j = 0; j < 5; ++j) {
            if (bb[j][word[j] - 'a']) { valid = false; break; }
            q.set(word[j] - 'a');
        }
        for (int j = 0; j < 26 && valid; ++j) {
            if (pp[j] && !q[j]) valid = false;
        }
        if (!valid) ++eliminated;
    }
    return eliminated;
}

// 单线程期望值计算函数
long double Expectedelimination(const string &guess) {
    int b_copy[10][26], p_copy[26];
    memcpy(b_copy, b, sizeof(b_copy));
    memcpy(p_copy, p, sizeof(p_copy));

    long double total = 0.0;
    for (const string& word : wo) {
        total += elimination(guess, word, b_copy, p_copy);
    }
    return total / wo.size();
}

// 推荐候选词
void suggest() {
    v1.clear();
    for (const string& i : aa) {
        long double ex = Expectedelimination(i);
        if (ex == 0) continue;
        v1.push_back({wo.size() - ex, {ex, i}});
    }
    sort(v1.begin(), v1.end());
}

int main() {
    freopen("a.in", "r", stdin);
    for (int i = 0; a[i] != ""; ++i) aa.push_back(a[i]);
    wo = aa;

    cout << "Enter your input:";
    while (cin >> s1) {
        if (s1 == "return") return 0;

        cout << "Enter the colour:";
        cin >> s2;

        for (char ch = 'a'; ch <= 'z'; ++ch) {
            vector<int> v1, v2, v3;
            for (int j = 0; j < 5; ++j) {
                if (s1[j] == ch) {
                    switch(s2[j]) {
                        case '1': v1.push_back(j); break;
                        case '2': v2.push_back(j); break;
                        case '3': v3.push_back(j); break;
                    }
                }
            }
            if (!v2.size() && v3.size()) for (int k = 0; k < 5; ++k) b[k][ch - 'a'] = 1;
            if (!v1.empty()) for (int pos : v1) { for (int j = 0; j < 26; ++j) b[pos][j] = 1; b[pos][ch - 'a'] = 0; }
            if (!v2.empty()) { p[ch - 'a'] = 1; for (int pos : v2) b[pos][ch - 'a'] = 1; }
            if (!v2.empty() && v3.size()) for (int pos : v3) b[pos][ch - 'a'] = 1;
        }

        for (int i = 0; i < aa.size(); ++i) {
            bool valid = true;
            for (int j = 0; j < 5; ++j) if (b[j][aa[i][j] - 'a']) valid = false;
            if (!valid) { swap(aa[i], aa.back()); aa.pop_back(); --i; }
        }

        int k = 0;
        for (int i = 0; i < wo.size(); ++i) {
            bool valid = true;
            bitset<26> q;
            for (int j = 0; j < 5; ++j) {
                if (b[j][wo[i][j] - 'a']) { valid = false; break; }
                q.set(wo[i][j] - 'a');
            }
            for (int j = 0; j < 26 && valid; ++j) {
                if (p[j] && !q[j]) valid = false;
            }
            if (valid) ++k;
            else { swap(wo[i], wo.back()); wo.pop_back(); --i; }
        }

        cout << "possible words(amount:" << k << "):\n";
        k = 0;
        for (int i = 0; i < wo.size() && k <= 10; ++i) {
            bool valid = true;
            bitset<26> q;
            for (int j = 0; j < 5; ++j) {
                if (b[j][wo[i][j] - 'a']) { valid = false; break; }
                q.set(wo[i][j] - 'a');
            }
            for (int j = 0; j < 26 && valid; ++j) {
                if (p[j] && !q[j]) valid = false;
            }
            if (valid) {
                ++k;
                cout << '\t' << wo[i] << '\n';
            }
        }

        if (wo.size() == v1.size()) { cout << "Nothing :)\n"; continue; }

        cout << "suggest words to narrow the range:\n";
        suggest();
        for (int i = 0; i < v1.size() && i < 5; ++i) {
            cout << fixed << setprecision(3) << v1[i].second.second << "  (expectly elimination " << v1[i].second.first << " words)\n";
        }
        cout << "Enter your input:";
    }
    return 0;
}
